import streamlit as st
import math

# Halaman utama
st.title('Aplikasi Perhitungan Luas Bangun Datar')
st.header('Ini buatan anak SI')

# Menu sidebar
menu = st.sidebar.selectbox(
    'Pilih Aplikasi',
    ['Luas Persegi', 'Luas Segitiga', 'Luas Lingkaran']
)


# 1. LUAS PERSEGI

if menu == 'Luas Persegi':
    st.write('Ini halaman untuk menghitung luas persegi')
    st.markdown(':orange[Luas Persegi]')
    st.image(
        'data:image/png;base64,',
        caption='Gambar Persegi')
    
    sisi = st.number_input('Silakan masukkan sisi', min_value=0)
    if st.button('Hitung Luas Persegi'):
        luas = sisi * sisi
        st.success(f'Luas persegi adalah {luas}')



# 2. LUAS SEGITIGA

elif menu == 'Luas Segitiga':
    st.write('Ini halaman untuk menghitung luas segitiga')
    st.markdown(':orange[Luas Segitiga]')
    st.image('data:image/jpeg;base64,', caption='Gambar Segitiga')

    alas = st.number_input('Masukkan alas', min_value=0)
    tinggi = st.number_input('Masukkan tinggi', min_value=0)

    if st.button('Hitung Luas Segitiga'):
        luas = 0.5 * alas * tinggi
        st.success(f'Luas segitiga adalah {luas}')



# 3. LUAS LINGKARAN

elif menu == 'Luas Lingkaran':
    st.write('Ini halaman untuk menghitung luas lingkaran')
    st.markdown(':orange[Luas Lingkaran]')
    
    st.image('data:image/png;base64,', caption='Gambar Lingkaran')

    r = st.number_input('Masukkan jari-jari (r)', min_value=0)

    if st.button('Hitung Luas Lingkaran'):
        luas = math.pi * r * r
        st.success(f'Luas lingkaran adalah {luas}')